var b = require('browserify')({
        standalone: "ConversationClient"
    }),
    fs = require('fs');

b.add("./lib/sdk");
b.bundle(function(err, source) {
    if (err) console.error(err);
    var dir = './dist/';

    if (!fs.existsSync(dir)) {
        fs.mkdirSync(dir);
    }
    fs.writeFileSync('./dist/conversationClient.js', source);
});